job.command:
starver &starVersion;

setenv PRODPATH &basePath;/production/&prodId;
setenv LOGPATH  &basePath;/jobs/&prodId;/log

mkdir -p ${PRODPATH} ${LOGPATH}

if ("&mMode;" == "1") then
     &basePath;/jobs/&prodId;/starSubmit/parseFileList.csh &listOfFiles; &basePath; &prodId; &mMode; &treeName; ${JOBID} &rootMacro; picoList_badRuns.list &productionBasePath; &decayChannel; &starVersion;
else if ("&mMode;" == "3") then
    root -q -b -l StRoot/macros/loadSharedHFLibraries.C StRoot/macros/&rootMacro;++\(\"&listOfFiles;\",\"&prodId;_$JOBINDEX\",\"picoList_badRuns.list\"\) >& ${JOBID}.log
    tar -zcvf ${JOBID}.log.tgz ${JOBID}.log
else
    root -q -b -l StRoot/macros/loadSharedHFLibraries.C StRoot/macros/&rootMacro;++\(\"&listOfFiles;\",\"&prodId;_$JOBINDEX\",&mMode;,\"picoList_badRuns.list\",\"&treeName;\",\"&productionBasePath;\",&decayChannel;\) >& ${JOBID}.log
endif
